import Head from 'next/head';
import DateRangePicker from '../../components/DateRangePicker';
import houses from '../../houses.json';
import Layout from '../../components/Layout';

const calcNumberOfNightsBetweenDates = (startDate, endDate) => {
  const start = new Date(startDate);
  const end = new Date(endDate);
  let dayCount = 0;

  while (end > start) {
    dayCount++;
    start.setDate(start.getDate() + 1);
  }
  return dayCount;
};

const [numberOfNightsBetweenDates, setNumberOfNightsBetweenDates] = useState(0);

const House = props => {
  const [dateChosen, setDateChosen] = useState(false); //create new dateChosen state propery, which defaults to false;

  return (
    <Layout
      content={
        <div className='container'>
          <Head>
            <title>{props.house.title}</title>
          </Head>
          <article>
            <h1>Welcome to {props.house.hostName || 'no name'}'s listing</h1>
            <img src={props.house.picture} width='70%' alt='House picture' />
            <p>
              {props.house.type} - {props.house.town}
            </p>
            <p>{props.house.title}</p>
            <p>
              {props.house.rating} ({props.house.reviewsCount})
            </p>
          </article>
          <aside>
            <h2>Add dates for prices</h2>
            <DateRangePicker
              datesChanged={(startDate, endDate) => {
                console.log('start', startDate, 'end', endDate);
                setNumberOfNightsBetweenDates(
                  calcNumberOfNightsBetweenDates(startDate, endDate)
                );
                setDateChosen(true);
              }}
            />
            {dateChosen && (
              <div>
                <h2>Price per night</h2>
                <p>${props.house.price}</p>
                <h2>Total price for booking</h2>
                <p>
                  ${(numberOfNightsBetweenDates * props.house.price).toFixed(2)}
                </p>
              </div>
            )}
          </aside>
          <style jsx>{`
            .container {
              display: grid;
              grid-template-columns: 60% 40%;
              grid-gap: 30px;
            }

            aside {
              border: 1px solid #ccc;
              padding: 20px;
            }
          `}</style>
        </div>
      }
    />
  );
};

House.getInitialProps = ({ query }) => {
  console.log(query);
  const { id } = query;

  return {
    house: houses.filter(house => house.id === id)[0],
  };
};

export default House;
